(function() { 
const xx = appsmith.store.editRow.registeredOn
const dateString = xx;
const dateParts = dateString.split("-"); // Split the string by hyphen

const year = parseInt(dateParts[0]); // Extract the year
const month = parseInt(dateParts[1]); // Extract the month
const dayOfWeek = dateParts[2].slice(0, 3); // Extract the day of the week

// Extract the time information
const timeString = dateParts[2].slice(4);
const timeParts = timeString.split(":");
const hours = parseInt(timeParts[0]);
const minutes = parseInt(timeParts[1]);
const seconds = parseInt(timeParts[2]);

// Create a new Date object with the extracted components
const date = new Date(year, month - 1, 1); // Use '1' as the day since the original string doesn't provide a specific day
date.setDate(date.getDate() - (date.getDay() - ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].indexOf(dayOfWeek)));

// Format the date in a simple format
const formattedDate = `${date.getFullYear()}-${(date.getMonth() + 1).toString().padStart(2, '0')}-${date.getDate().toString().padStart(2, '0')}`;
const formattedTime = `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;

console.log(`${formattedDate} ${formattedTime}`);
 return formattedDate;
})()
}}

